import React from "react";
import "./CourseDetails.css";

/**
 * Course Details Modal Component
 * ÊòæÁ§∫ËØæÁ®ãËØ¶ÁªÜ‰ø°ÊÅØÁöÑÊ®°ÊÄÅÊ°Ü
 */
const CourseDetails = ({ course, isOpen, onClose }) => {
  if (!isOpen || !course) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const formatPrerequisites = (prereqs) => {
    if (!prereqs || prereqs.length === 0) return "Êó†";
    return prereqs.join(", ");
  };

  const renderScheduleInfo = () => {
    if (!course.sessions || course.sessions.length === 0) {
      return <p className="no-schedule">ÊöÇÊó†ÊéíËØæ‰ø°ÊÅØ</p>;
    }

    return course.sessions.map((session, index) => (
      <div key={index} className="session-info">
        <div className="session-type">{session.type}</div>
        <div className="session-details">
          <span className="session-time">
            {session.day} {session.time} ({session.duration}Â∞èÊó∂)
          </span>
          {session.location && (
            <span className="session-location">üìç {session.location}</span>
          )}
          {session.instructor && (
            <span className="session-instructor">üë®‚Äçüè´ {session.instructor}</span>
          )}
        </div>
      </div>
    ));
  };

  const renderRating = (rating) => {
    if (!rating) return "ÊöÇÊó†ËØÑÂàÜ";

    const stars =
      "‚òÖ".repeat(Math.floor(rating)) + "‚òÜ".repeat(5 - Math.floor(rating));
    return (
      <div className="rating-display">
        <span className="stars">{stars}</span>
        <span className="rating-number">({rating}/5)</span>
      </div>
    );
  };

  return (
    <div className="modal-backdrop" onClick={handleBackdropClick}>
      <div className="course-details-modal">
        <div className="modal-header">
          <div className="course-title-section">
            <h2 className="course-code">{course.code}</h2>
            <h3 className="course-name">{course.name}</h3>
          </div>
          <button className="close-button" onClick={onClose}>
            ‚úï
          </button>
        </div>

        <div className="modal-content">
          <div className="course-basic-info">
            <div className="info-row">
              <label>Â≠¶ÂàÜÔºö</label>
              <span>{course.credits || 6} UOC</span>
            </div>
            <div className="info-row">
              <label>Â≠¶Èô¢Ôºö</label>
              <span>{course.faculty || "Engineering"}</span>
            </div>
            <div className="info-row">
              <label>ËØæÁ®ãÁ±ªÂûãÔºö</label>
              <span className={`course-type ${course.type?.toLowerCase()}`}>
                {course.type || "Core"}
              </span>
            </div>
            <div className="info-row">
              <label>ËØÑÂàÜÔºö</label>
              {renderRating(course.rating)}
            </div>
          </div>

          <div className="course-description">
            <h4>ËØæÁ®ãÊèèËø∞</h4>
            <p>{course.description || "ÊöÇÊó†ËØæÁ®ãÊèèËø∞"}</p>
          </div>

          <div className="prerequisites-section">
            <h4>ÂÖà‰øÆË¶ÅÊ±Ç</h4>
            <p>{formatPrerequisites(course.prerequisites)}</p>
          </div>

          <div className="schedule-section">
            <h4>ËØæÁ®ãÂÆâÊéí</h4>
            {renderScheduleInfo()}
          </div>

          {course.assessments && course.assessments.length > 0 && (
            <div className="assessments-section">
              <h4>ËÄÉÊ†∏ÊñπÂºè</h4>
              <ul className="assessment-list">
                {course.assessments.map((assessment, index) => (
                  <li key={index} className="assessment-item">
                    <span className="assessment-type">{assessment.type}</span>
                    <span className="assessment-weight">
                      {assessment.weight}%
                    </span>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {course.outcomes && course.outcomes.length > 0 && (
            <div className="outcomes-section">
              <h4>Â≠¶‰π†ÊàêÊûú</h4>
              <ul className="outcomes-list">
                {course.outcomes.map((outcome, index) => (
                  <li key={index}>{outcome}</li>
                ))}
              </ul>
            </div>
          )}

          <div className="course-stats">
            <div className="stat-item">
              <label>ÂéÜÂè≤ÈÄöËøáÁéáÔºö</label>
              <span>{course.passRate || "85"}%</span>
            </div>
            <div className="stat-item">
              <label>Âπ≥ÂùáÊàêÁª©Ôºö</label>
              <span>{course.averageGrade || "Credit"}</span>
            </div>
            <div className="stat-item">
              <label>ËØæÁ®ãÈöæÂ∫¶Ôºö</label>
              <span
                className={`difficulty ${
                  course.difficulty?.toLowerCase() || "medium"
                }`}
              >
                {course.difficulty || "Medium"}
              </span>
            </div>
          </div>
        </div>

        <div className="modal-footer">
          <button className="btn-secondary" onClick={onClose}>
            ÂÖ≥Èó≠
          </button>
          <button
            className="btn-primary"
            onClick={() => {
              // Ê∑ªÂä†Âà∞Êó∂Èó¥Ë°®ÁöÑÈÄªËæë
              onClose();
            }}
          >
            Ê∑ªÂä†Âà∞Êó∂Èó¥Ë°®
          </button>
        </div>
      </div>
    </div>
  );
};

export default CourseDetails;
